        this.pos.y = rect.pos.y
        this.dim.width = rect.dim.width
        this.dim.height = rect.dim.height

        this.invalidate(rect)
        this.$div.css({
          position: 'relative',
          left: rect.pos.x,
          top: rect.pos.y,
          width: rect.dim.width,
          height: rect.dim.height
        })
