export function formatTime(t,o){const e=Math.floor(t%1*(o||0));let r=Math.floor(t),a=Math.floor(r/60);const l=Math.floor(a/60),n=t=>t<10?`0${t}`:t;return a%=60,r%=60,`${l>0?`${n(l)}:`:""}${n(a)}:${n(r)}${e?`:${n(e)}`:""}`};export function timeToSeconds(t){const o=t.replace(/;/g,":").split(":");let e=0;return 3===o.length?(e+=60*parseFloat(o[0])*60,e+=60*parseFloat(o[1]),e+=parseFloat(o[2])):(e+=60*parseFloat(o[0]),e+=parseFloat(o[1])),e};