process.on("message",message=>{let args=Object.keys(message).filter(function(e){return e.match(/^argument/)}).sort(function(e,s){return parseInt(e.slice(8),10)-parseInt(s.slice(8),10)}).map(function(e){return message[e]});try{let result=eval("("+message.func+")").apply(null,args);"undefined"!=typeof Promise&&result instanceof Promise?result.then(function(e){process.send({id:message.id,result:e})}).catch(function(e){process.send({id:message.id,error:e.stack})}):process.send({id:message.id,result:result})}catch(e){process.send({id:message.id,error:e.stack})}});